function dbg(a){window.console.log(a)}function ajax(options){var req=null,paramsCount=0,arrayParams=[],params="";req=(window.XMLHttpRequest)?new XMLHttpRequest():new ActiveXObject("Microsoft.XMLHTTP");req.onreadystatechange=function(){if(req.readyState===4&&req.status===200){if(options.dataType=="json"){options.success(JSON.parse(req.response))}else{options.success(eval("("+req.response+")"))}}};req.open(options.type,options.url,true);if(options.type.toLowerCase()=="post"){for(var x in options.data){arrayParams[paramsCount]=x+"="+options.data[x];paramsCount++}req.setRequestHeader("Content-type","application/x-www-form-urlencoded");req.send(arrayParams.join("&"))}else{req.send()}};